sys_variable_value.value:
(function execute(inputs, outputs) {
	var payload = JSON.parse(inputs.countries);
  	var countryNames = payload.map(function(country) {
      return { label: country.name, name: country.name };
    });
  	outputs.countrynames = {data : countryNames};
})(inputs, outputs);

